import { Fragment, useContext, useState, useEffect } from 'react';
import { Outlet } from 'react-router-dom';

import CartIcon from '../../components/cart-icon/cart-icon.component';
import CartDropdown from '../../components/cart-dropdown/cart-dropdown.component';

import { UserContext } from '../../contexts/user.context';
import { CartContext } from '../../contexts/cart.context';

import { ReactComponent as CrwnLogo } from '../../assets/crown.svg';
import { signOutUser } from '../../utils/firebase/firebase.utils';

import SearchBox from '../../components/search-box/search-box.component';

import {
  NavigationContainer,
  NavLinks,
  NavLink,
  LogoContainer,
} from './navigation.styles';

const Navigation = () => {
	const { currentUser } = useContext(UserContext);
	const { isCartOpen } = useContext(CartContext);
	const [searchField, setSearchField] = useState('');
	const [ dreams, setDreams] = useState([]);
	const [filteredDreams, setFilterDreams] = useState(dreams);

	useEffect( () => {
		fetch('https://jsonplaceholder.typicode.com/albums')
			.then( (response) => response.json() )
			.then( (albums) => setDreams( albums ) );
	}, [] );

	useEffect( () => {
		const newFilteredDreams = dreams.filter( (dream) => {
			return dream.name.toLocaleLowerCase().includes(searchField);
		});

		setFilterDreams(newFilteredDreams);
	}, [dreams, searchField]);

	const onSearchChanges = (event) => {
		const searchFieldString = event.target.value.toLocaleLowerCase();
		setSearchField(searchFieldString);
	};

  return (
    <Fragment>
      <NavigationContainer>
        <LogoContainer to='/'>
          <CrwnLogo className='logo' />
        </LogoContainer>
		<SearchBox
			className='dreams-search-box'
			onChangeHandler={onSearchChange}
			placeholder='search dreams'
		/>
        <NavLinks>
          <NavLink to='/shop'>SHOP</NavLink>

          {currentUser ? (
            <NavLink as='span' onClick={signOutUser}>
              SIGN OUT
            </NavLink>
          ) : (
            <NavLink to='/auth'>SIGN IN</NavLink>
          )}
          <CartIcon />
        </NavLinks>
        {isCartOpen && <CartDropdown />}
      </NavigationContainer>
      <Outlet />
    </Fragment>
  );
};

export default Navigation;
